import React, { useEffect, useState } from "react";
import "../styles/Home.css";

function Home() {
  const [showSkills, setShowSkills] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      const skillsSection = document.getElementById("skills");
      const skillsSectionPosition = skillsSection.getBoundingClientRect().top;
      const windowHeight = window.innerHeight;

      if (skillsSectionPosition < windowHeight * 0.8) {
        setShowSkills(true);
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <div className="home">
      <div className="about">
        <h2>Hello, my name is Christian</h2>
        <div className="prompt">
          <p>A web developer with a passion for learning.</p>
        </div>
      </div>
      <div className="skills">
        <h1>Skills</h1>
        <ol>
          <li className="softskill">
            <h3> Front-End </h3>
            <p>ReactJS, HTML, CSS, NPM, BootStrap</p>
            <h3> Back-End </h3>
            <p>NodeJS, MySQL, NoSQL, GraphQL, ExpressJS, ApolloServer</p>
            <h3> Languages </h3>
            <p>Javascript, TypeScript</p>
          </li>
        </ol>
      </div>
    </div>
  );
}
export default Home;
